// Tree Recursion --> function call itself more than one time

#include <iostream>
#include <stdio.h>

using namespace std;

void func(int n)
{
    if (n > 0)
    {
        printf("%d\n", n);
        // statement - 1
        // statement - 2
        func(n - 1);
        // statement - 3
        // statement - 4
        func(n - 1);
        // statement - 5
        // statement - 6
    }
}

// for this function in stack

// |-------------------------------------------------------------------|
// |    n = 0 , n = 0 , n = 0 , n = 0 , n = 0 , n = 0 , n = 0 , n = 0  |    <----- terminated and activation recored is deleted, control goes back to previous -----
// |-------------------------------------------------------------------|                                                                                            |
// |    n = 1 , n = 1 , n = 1                                          |    <----- calling func                                                     <---------------
// |-------------------------------------------------------------------|
// |    n = 2 , n = 2                                                  |    <----- calling func
// |-------------------------------------------------------------------|
// |    n = 3                                                          |    <----- calling func
// |-------------------------------------------------------------------|

// output - 3 2 1 1 2 1 1

//
// func(3) ----------------------           <------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
//         |         |           |                                                                                                                                                                                                                                                                                                |
//        |         |           |                                                                                                                                                                                                                                                                                                 |
//        3         |         func(2) --------------------------------------------------------------------------------------------------------------------------------------------------------------------------------                                                                                                            |
//                 |            ^                                                                                                                                                         |               |           |                                                                                                           |
//                 |            |                                                                                                                                                         |               |           |                                                                                                           |
//                |              -----------------------------------------------------------------------------------------------------------------------                                  2               |         func(1) ----------------------------------------------------------------------------------                    |
//              func(2) ----------------------                                                                                                          |                                                 |             ^                                                             |          |             |                  |
//                     |         |           |                                                                                                          |                                                 |             |                                                             |          |             |                  |
//                    |         |           |                                                                                                           |                                                 |              --------------------------                                   1          |           func(0) ---> function terminate      <------
//                    2         |         func(1) ----------------------------------------------------------------------------------                    |                                              func(1) ----------------------               |                                            |                                                       |
//                             |             ^                                                             |          |             |                   |                                                      |          |         |               |                                            |                                                       |
//                             |             |                                                             |          |             |                   |                                                      |          |         |               |                                            |                                                       |
//                            |               --------------------------                                   1          |           func(0) ---> function terminate      <------                                 1          |       func(0) ---> function terminate      <----                   func(0) ---> function call terminate --------------------
//                         func(1) ----------------------              |                                              |                                                       |                                           |                                                 |
//                                |         |           |              |                                              |                                                       |                                           |                                                 |
//                               |         |           |               |                                              |                                                       |                                           |                                                 |
//                               1         |          func(0) ---> function terminate      <-----                   func(0) ---> function call terminate --------------------                                           func(0) ---> function call terminate ---------------
//                                        |                                                     |
//                                        |                                                     |
//                                       |                                                     |
//                                    func(0) ---> function call terminate --------------------
//

//  for "n = 3" there are four levels
// 1 - level --> 1 call
// 2 - level --> 2 call
// 3 - level --> 4 call
// 4 - level --> 8 call
// sum = 1 + 2 + 4 +8 = 15
// 2^0 + 2^1 +2^2 +2^3 =15
// or sum of geometric progression
// we can write it as
// 2^3 -1

// so for "n" it will --> 2^n -1
// no. of call is O(2^n)
// time complexity = O(2^n)
// space will be height of the tree
// space complexity = O(n)